pragma solidity 0.8.7;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import "./interfaces/IWrappedToken.sol";
import "./interfaces/ISilo.sol";
import "./interfaces/IRepository.sol";

contract SiloRouter {
    using SafeERC20 for IERC20;

    enum ActionType { Deposit, Withdraw, Borrow, Repay }

    struct Action {
        // what do you want to do?
        ActionType actionType;
        // which Silo are you internacting with?
        address silo;
        // what asset do you want to use?
        address asset;
        // how much asset do you want to use?
        uint256 amount;
        // is it an action on collateral only?
        bool collateralOnly;
    }

    IWrappedToken public immutable wrappedToken;

    constructor (IWrappedToken _wrappedToken) {
        wrappedToken = _wrappedToken;
    }

    receive() external payable {}

    function borrow(Action[] memory _actions) external payable {
        uint256 len = _actions.length;

        for (uint256 i  = 0; i < len; i++) {
            _executeAction(_actions[i], i == 0, i == len - 1);
        }
    }

    /// @param _action action to execute, this can be one of many actions in the whole flow
    /// @param _first is this FIRST action in a flow
    /// @param _first is this LAST action in a flow
    // solhint-disable-next-line code-complexity
    function _executeAction(Action memory _action, bool _first, bool _last) internal {
        if (_action.actionType == ActionType.Deposit) {
            if (_first) {
                _pullAsset(_action.asset, _action.amount);
            }

            ISilo(_action.silo).depositFor(_action.asset, msg.sender, _action.amount, _action.collateralOnly);
        } else if (_action.actionType == ActionType.Withdraw) {
            ISilo(_action.silo).withdrawFor(
                _action.asset,
                msg.sender,
                address(this),
                _action.amount,
                _action.collateralOnly
            );

            if (_last) {
                _sendAsset(_action.asset, _action.amount);
            }
        } else if (_action.actionType == ActionType.Borrow) {
            ISilo(_action.silo).borrowFor(_action.asset, msg.sender, address(this), _action.amount);

            if (_last) {
                _sendAsset(_action.asset, _action.amount);
            }
        } else if (_action.actionType == ActionType.Repay) {
            if (_first) {
                _pullAsset(_action.asset, _action.amount);
            }

            ISilo(_action.silo).repayFor(_action.asset, msg.sender, _action.amount);
        } else {
            revert("SiloRouter: unsupported action");
        }
    }

    function _pullAsset(address _asset, uint256 _amount) internal {
        if (_asset == address(wrappedToken) && msg.value > 0) {
            require(msg.value == _amount, "SiloRouter: incorrect asset value");
            wrappedToken.deposit{value: msg.value}();
        } else {
            IERC20(_asset).safeTransferFrom(msg.sender, address(this), _amount);
        }
    }

    function _sendAsset(address _asset, uint256 _amount) internal {
        if (_asset == address(wrappedToken)) {
            wrappedToken.withdraw(_amount);
            // solhint-disable-next-line avoid-low-level-calls
            (bool success, ) = msg.sender.call{value: _amount}("");
            require(success, "SiloRouter: asset transfer failed.");
        } else {
            IERC20(_asset).safeTransfer(msg.sender, _amount);
        }
    }
}
