pragma solidity 0.8.7;

import "../interfaces/IOracle.sol";
import "../oracles/Oracle.sol";

contract MockOracle is Oracle {
    mapping(address => uint256) public prices;
    mapping(address => uint256) public liquidity;

    constructor(address _quoteToken) Oracle(_quoteToken) {
    }

    function setPrice(address _asset, uint256 _price) public {
        prices[_asset] = _price;
    }

    function setLiquidity(address _asset, uint256 _liquidity) public {
        prices[_asset] = _liquidity;
    }

    function getPrice(address _asset) external override returns (uint256) {
        return prices[_asset];
    }

    function getQuoteLiquidity(address _asset) external view override returns (uint256) {
        return liquidity[_asset];
    }
}
