pragma solidity 0.8.7;

import "./ShareToken.sol";
import "../interfaces/ISilo.sol";
import "../Error.sol";

contract ShareCollateralToken is ShareToken {
    constructor (
        string memory _name,
        string memory _symbol,
        address _silo,
        address _asset
    ) ERC20(_name, _symbol) ShareToken(_silo, _asset) {}

    function _beforeTokenTransfer(address _sender, address _recipient, uint256) internal view override {
        // if we minting or burning, Silo is responsible to check all necessary conditions
        if (!_isTransfer(_sender, _recipient)) {
            return;
        }

        if (!silo.depositPossible(asset, _recipient)) {
            revert ShareTransferNotAllowed();
        }
    }

    function _afterTokenTransfer(address _sender, address _recipient, uint256) internal view override {
        // if we minting or burning, Silo is responsible to check all necessary conditions
        if (_isTransfer(_sender, _recipient)) {
            require(silo.isSolvent(_sender), "Transfer Collateral: sender not solvent after transfer");
        }
    }
}
