pragma solidity >=0.6.0 <0.9.0;

import "../interfaces/IOracle.sol";

/// @title Abstract oracle contract, parent of all Silo oracles
abstract contract Oracle is IOracle {
    /// @notice It must be a copy of SiloBridgePool.asset
    address public immutable override quoteToken;

    /// @notice Common event for any asses modifications (initialization, save)
    /// @dev Must be emitted every time something change for asset eg:
    ///      - on initialization
    ///      - on changing pool or some dedicated settings for asses
    event AssetOperation(address asset);

    constructor(address _quoteToken) {
        require(_quoteToken != address(0), "Oracle: empty quoteToken");

        quoteToken = _quoteToken;
    }

    function initAsset(address, bytes calldata) external virtual override {}

    function isSiloOracle() external pure override returns (bool) {
        return true;
    }
}
