pragma solidity 0.7.6;

import "@uniswap/v3-periphery/contracts/libraries/OracleLibrary.sol";
import "@uniswap/v3-core/contracts/interfaces/IUniswapV3Factory.sol";
import "@uniswap/v3-core/contracts/interfaces/IUniswapV3Pool.sol";

import "../Oracle.sol";
import "./TwoStepOwnable.sol";

interface IERC20 {
    function decimals() external view returns (uint8);
    function balanceOf(address) external view returns(uint256);
}

contract UniswapV3Oracle is Oracle, TwoStepOwnable {
    /// @dev blockchain block time
    uint256 public constant BLOCK_TIME = 13;

    IUniswapV3Factory public immutable uniswapV3Factory;

    // Number of seconds in the past to start calculating time-weighted average
    uint32 public periodForAvgPrice;
    uint24[] public fees;

    mapping(uint24 => bool) public knownFees;

    /// address of asset => fee for asset, that can be used for resolving price or swap
    mapping(address => uint24) public assetsFees;

    event NewFee(uint24 fee);
    event NewPeriod(uint32 period);
    event FeeForAsset(address base, uint24 fee);

    constructor(address _quoteToken, IUniswapV3Factory _factory, uint32 _periodForAvgPrice, uint24[] memory _fees)
        Oracle(_quoteToken)
    {
        require(_factory.feeAmountTickSpacing(500) >= 0, "UniswapV3Oracle: invalid factory");
        require(_periodForAvgPrice > 0, "UniswapV3Oracle: invalid periodForAvgPrice");

        uniswapV3Factory = _factory;

        periodForAvgPrice = _periodForAvgPrice;
        fees = _fees;

        for (uint256 i = 0; i < _fees.length; i++) {
            knownFees[_fees[i]] = true;
        }
    }

    /// @param _data (optional) encoded fee, if provided we will save that fee
    /// @inheritdoc IOracle
    function initAsset(address _asset, bytes calldata _data) external virtual override {
        if (_data.length > 0) {
            (uint24 fee) = abi.decode(_data, (uint24));

            // check if it is know, so we do not throw
            if (!knownFees[fee]) {
                notifyAboutNewFee(fee);
            }
        }

        _chooseBestFeeForAsset(_asset);
        _adjustOracleCardinality(_asset);
    }

    /// @dev there is possibility, that when we change this period, UniV3 pool that is already initialized and set
    ///         as oracle for asset, can throw. This can happen when it will not be able calculate TWAP for new period
    ///         and it can potentially lock the Silo until we have necessary observations. If UniV3 is NOT only
    ///         available oracle for asset, we can change the oracle and Silo will be unlock.
    function changePeriodForAvgPrice(uint32 _period) external onlyOwner {
        periodForAvgPrice = _period;
        emit NewPeriod(_period);
    }

    function notifyAboutNewFees(uint24[] calldata _fees) external {
        for (uint256 i = 0; i < _fees.length; i++) {
            notifyAboutNewFee(_fees[i]);
        }
    }

    /// @inheritdoc IOracle
    function notifyAboutChoice(address _asset) external override {
        _chooseBestFeeForAsset(_asset);
        _adjustOracleCardinality(_asset);
    }

    /// @dev this could be used to manually setup pool, so it will be ready to use in future
    ///         There is only one case, when it could be needed: if we have more than one pools for `_asset`
    ///         and you want to use UniswapV3 but pool for this asset has not enough observations (liquidity == 1)
    ///         you can run this method manually and it will set valid number of observations, so UniswapV3 can be used
    ///         once price data will be ready
    function adjustOracleCardinality(address _asset) external {
        _adjustOracleCardinality(_asset);
    }

    function hasEnoughObservations(address _pool) external view returns (bool, uint32 oldestObservationTimestamp) {
        return _hasEnoughObservations(_pool);
    }

    function assetSupported(address _asset) external view override returns (bool supported) {
        (, supported) = findFeeForAsset(_asset);
    }

    function getQuoteLiquidity(address _asset) external view override returns (uint256 quoteLiquidity) {
        return _getQuoteLiquidity(_asset, false);
    }

    function getQuoteLiquidityRaw(address _asset) external view override returns (uint256 quoteLiquidity) {
        return _getQuoteLiquidity(_asset, true);
    }

    /// @dev this additional view can be used for asset verifier
    function poolQuoteLiquidity(address _pool) external view returns (uint256) {
        uint24 fee = IUniswapV3Pool(_pool).fee();
        address token0 = IUniswapV3Pool(_pool).token0();
        address token1 = IUniswapV3Pool(_pool).token1();

        (address asset, address quote) = quoteToken == token0 ? (token1, token0) : (token0, token1);

        return _poolQuoteLiquidity(asset, quote, fee, true);
    }

    function notifyAboutNewFee(uint24 _fee) public {
        int24 tickSpacing = uniswapV3Factory.feeAmountTickSpacing(_fee);

        require(!knownFees[_fee], "UniswapV3Oracle.notifyAbutNewFee: that fee already exists");
        require(tickSpacing != 0, "UniswapV3Oracle.notifyAbutNewFee: fee does not exist");

        emit NewFee(_fee);

        fees.push(_fee);
        knownFees[_fee] = true;
    }

    /// @notice Base on known fees
    /// @return fee for asset and information if search was successful eg:
    ///         - if successful is false, means we were not able to find a fee for asses, fee value will be always 0
    ///         - if successful is true, we were bale to find a fee for asses, fee value will be >=0
    function findFeeForAsset(address _asset) public view returns (uint24 fee, bool success) {
        address quote = quoteToken;
        uint256 bestFeeId;
        uint256 quoteLiquidity;

        for (uint256 i = 0; i < fees.length; i++) {
            address pool = IUniswapV3Factory(uniswapV3Factory).getPool(_asset, quote, fees[i]);
            if (pool == address(0)) continue;

            uint256 liquidity = IERC20(quote).balanceOf(pool);

            if (liquidity > quoteLiquidity) {
                quoteLiquidity = liquidity;
                bestFeeId = i;
            }
        }

        if (quoteLiquidity != 0) {
            return(fees[bestFeeId], true);
        }
    }

    /**
        @notice UniV3 saves price only on: mint, burn and swap.
        Mint and burn will write observation only when "current tick is inside the passed range" of ticks.
        I think that means, that if we minting/burning outside ticks range  (so outside current price)
        it will not modify observation. So we left with swap.

        Swap will write observation under this condition:
            // update tick and write an oracle entry if the tick change
            if (state.tick != slot0Start.tick) {
        that means, it is possible that price will be up to date (in a range of same tick)
        but observation timestamp will be old.

        Every pool by default comes with just one slot for observation (cardinality == 1).
        We can increase number of slots so TWAP price will be "better".
        When we increase, we have to wait until new tx will write new observation.

        Based on all above, we can tell how old is observation, but this does not mean the price is wrong.
        UniV3 recommends to use `observe` and `OracleLibrary.consult` uses it.
        `observe` reverts if `secondsAgos` > oldest observation, means, if there is any price observation in selected
        time frame, it will revert. Otherwise it will return either exact TWAP price or by interpolation.

        Conclusion: we can choose how many observation pool will be storing, but we need to remember,
        not all of them might be used to provide our price. Final question is: how many observations we need?

        How UniV3 calculates TWAP
        we ask for TWAP on time range ago:now using `OracleLibrary.consult`, it is all about find the right tick
        - we call `IUniswapV3Pool(pool).observe(secondAgos)` that returns two accumulator values (for ago and now)
        - each observation is resolved by `observeSingle`
          - for _now_ we just using latest observation, and if it does not match timestamp, we interpolate (!)
            and this is how we got the _tickCumulative_, so in extreme situation, if last observation was made day ago,
            UniV3 will interpolate to reflect _tickCumulative_ at current time
          - for _ago_ we search for observation using `getSurroundingObservations` that give us
            before and after observation, base on which we calculate "avg" and we have target _tickCumulative_
            - getSurroundingObservations: it's job is to find 2 observations based on which we calculate tickCumulative
              here is where all calculations can revert, if ago < oldest observation, otherwise it will be calculated
              either by interpolation or we will have exact match
        - now with both _tickCumulative_s we calculating TWAP

        recommended observations are = 30 min / blockTime  ?

        @inheritdoc IOracle
    */
    function getPrice(address _asset) public view override returns (uint256 price) {
        address quote = quoteToken;
        uint256 baseAmount = 10 ** IERC20(_asset).decimals();

        if (_asset == quote) return baseAmount;

        IUniswapV3Pool pool = _resolvePool(_asset, quote);

        // Number of seconds in the past to start calculating time-weighted average
        // UniV3 recommends to use `observe` and `OracleLibrary.consult` uses it.
        // `observe` reverts, if `secondsAgos` < oldest observation, means, if we asking for OLD price
        int24 timeWeightedAverageTick = OracleLibrary.consult(address(pool), periodForAvgPrice);
        price = OracleLibrary.getQuoteAtTick(timeWeightedAverageTick, uint128(baseAmount), _asset, quote);
    }

    /// @dev increases observation cardinality for univ3 oracle pool if needed, see getPrice desc for details
    ///         we should call it on init and when we changing the pool (univ3 can have multiple pools for same tokens)
    ///         IMPORTANT: when asking for price, oracle will throw, if we will not have enough observations,
    ///         hence it is possible to lock silo if we choose to use UniV3 with not enough observations.
    ///         Solution: return 1 as liquidity for the pool until we have all required observations.
    ///         1 will allow to initialise Silo in case this is the only oracle, but we will have to wait until we can
    ///         use Silo.
    /// @param _asset asset address
    function _adjustOracleCardinality(address _asset) internal {
        IUniswapV3Pool pool = _resolvePool(_asset, quoteToken);
        (,,,, uint16 cardinalityNext,,) = pool.slot0();

        // ideally we want to have data at every block during periodForAvgPrice
        uint256 requiredCardinality = periodForAvgPrice / BLOCK_TIME;

        if (cardinalityNext >= requiredCardinality) {
            return;
        }

        // initialize required amount of slots, it will cost!
        pool.increaseObservationCardinalityNext(uint16(requiredCardinality));
    }

    function _chooseBestFeeForAsset(address _asset) internal {
        (uint24 fee, bool success) = findFeeForAsset(_asset);
        require(success, "UniswapV3Oracle._chooseBestFeeForAsset: can't find fee for asset");

        assetsFees[_asset] = fee;

        emit FeeForAsset(_asset, fee);
        emit AssetOperation(_asset);
    }

    /// @param _asset asset address
    /// @param _raw set it to FALSE for protocol, use TRUE only for off-chain checks
    function _getQuoteLiquidity(address _asset, bool _raw) internal view returns (uint256 quoteLiquidity) {
        address quote = quoteToken;

        if (_asset == quote) return 0;

        for (uint256 i = 0; i < fees.length; i++) {
            uint256 liquidity = _poolQuoteLiquidity(_asset, quote, fees[i], _raw);

            if (liquidity > quoteLiquidity) {
                quoteLiquidity = liquidity;
            }
        }
    }

    function _poolQuoteLiquidity(address _asset, address _quote, uint24 _fee, bool _raw)
        internal
        view
        returns (uint256)
    {
        address pool = IUniswapV3Factory(uniswapV3Factory).getPool(_asset, _quote, _fee);
        if (pool == address(0)) return 0;

        if (_raw) return IERC20(_quote).balanceOf(pool);

        // we are using 1 if pool is not "ready" for calculating TWAP, because we do not want to revert on oracle
        // operations, so if this pool is the ONLY possible choice, it can be used
        (bool hasEnough, ) = _hasEnoughObservations(pool);
        return hasEnough ? IERC20(_quote).balanceOf(pool) : uint256(1);
    }

    function _resolvePool(address _asset, address _quote) internal view returns (IUniswapV3Pool pool) {
        pool = IUniswapV3Pool(IUniswapV3Factory(uniswapV3Factory).getPool(_asset, _quote, assetsFees[_asset]));
        require(address(pool) != address(0), "UniswapV3Oracle: pool not found");
    }

    /// @dev checks, if pool is ready to provide TWAP for required `periodForAvgPrice`
    function _hasEnoughObservations(address _pool) internal view returns (bool, uint32 oldestObservationTimestamp) {
        (,, uint16 index, uint16 cardinality,,,) = IUniswapV3Pool(_pool).slot0();

        bool initialized;

        (
            oldestObservationTimestamp,,,
            initialized
        ) = IUniswapV3Pool(_pool).observations((index + 1) % cardinality);

        // if not initialized, we just check id#0 as this will be the oldest
        if (!initialized) (oldestObservationTimestamp,,,) = IUniswapV3Pool(_pool).observations(0);

        if (oldestObservationTimestamp == 0) return (false, oldestObservationTimestamp);

        return ((block.timestamp - periodForAvgPrice) >= oldestObservationTimestamp, oldestObservationTimestamp);
    }
}
